package JavaBasics;

public class FinalConcept {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*final keywords is used to declare constant variable 
		 * To avoid inheritance, if class is created with final keyword can not be extend by child class 
		 * To avoid the method overriding, if method is created with final keyword  
		 * */
		
		final int i =  10;
		System.out.println(i);
		//i =20;
		System.out.println(i);
		
		

	}

}
